function page($tbl, $p, $l, $s)
{
    global $link;

    // 從第幾筆開始查詢，SQL的limit第一個數字從0開始算
    // "LIMIT 0,5" 代表跳過0筆資料取5筆
    // 頁數 |   SQL LIMIT第一個數字
    //  1   |   1*5-5=0
    //  2   |   2*5-5=5
    $start = $p*$l-$l;

    return sql($tbl, $s)." limit ".$start.", ".$l;
}

